import React, { Fragment, Component } from "react";
import "./App.css";

//function based component
// const App = () => {

//class based component
class App extends Component {
  echoName = name => {
    //you can't use function or const
    return "Hello " + name;
  };
  render() {
    const name = "Shayan Abedi";
    const sqrt = x => {
      return x ** 2;
    };
    return (
      <div className="App">
        <h1>Github finder!</h1>
        <p>Made by {name}</p>
        <p>Square root of 2 is {sqrt(2)}</p>
        <h3>{this.echoName("shayan")}</h3>
      </div>
    );
  }
}

export default App;
